package com.anilt.problems.stringmanipulation;

public class LongestSubSequenceStr 
{

	public static void main(String[] args) 
	{
		String s1 = "";
		String s2 = "";
		System.out.println(commonChild(s1, s2));
	}
	static int commonChild(String s1, String s2) 
	{
		int[][] dp = new int[s1.length()][s2.length()];
		return findLengthStr(dp, s1, s2, 0, 0);
    }
	static int findLengthStr(int[][] dp, String s1, String s2, int i1, int i2)
	{
		if(i1 == s1.length() || i2 == s2.length())
			return 0;
		if(dp[i1][i2] == 0)
		{
			if(s1.charAt(i1) == s2.charAt(i2))
			{
				dp[i1][i2] = 1+findLengthStr(dp, s1, s2, i1+1, i2+1);
				return dp[i1][i2];
			}
			else 
			{
				int iter2 = findLengthStr(dp, s1, s2, i1, i2+1);
				int iter3 = findLengthStr(dp, s1, s2, i1+1, i2);
				dp[i1][i2] = Math.max(iter2,  iter3);
			}
		}
		return dp[i1][i2];
	}
	public int findCommonStr(String s1, String s2) 
	{
		int[][] resultArr = new int[s1.length() + 1][s2.length() + 1];
		for (int i = s1.length(); i >= 1; i--) 
		{
			for (int j = s2.length(); j >= 1; j--) 
			{
				if (s1.charAt(i-1) == s2.charAt(j-1)) 
				{
					resultArr[i-1][j-1] =  Math.max((1 + resultArr[i][j]), Math.max(resultArr[i][j-1], resultArr[i-1][j]));
				}
				else 
				{
					resultArr[i-1][j-1] = Math.max(resultArr[i][j-1], resultArr[i-1][j]);
				}
			}
		}
		return resultArr[0][0];
	}
}
